import React from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";
import { useState } from "react";

const NewExpense = (props) => {
  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddExpense(expenseData);
  };

  const [formIsShown, setFormIsShown] = useState(false);

  const changeFormState = () => {
    if (formIsShown === false) {
      setFormIsShown(true);
    } else {
      setFormIsShown(false);
    }
  };

  const cancelForm = () => {
    setFormIsShown(false);
  };

  const cancel = <button onClick={changeFormState}>Cancel</button>;

  const checkFormIsShown = () => {
    if (formIsShown === true) {
      return (
        <div>
          <ExpenseForm onAddExpense={saveExpenseDataHandler} />
          <button onClick={cancelForm}>Cancel</button>
        </div>
      );
    } else return <ExpenseForm />;
  };
  let formState = checkFormIsShown();

  return <div className="new-expense">{formState}</div>;
};

export default NewExpense;
